import { createSlice } from "@reduxjs/toolkit";

const initialState = {
  items: [...new Array(50)],
  isLiked: [],
};

export const productSlice = createSlice({
  name: "product",
  initialState,
  reducers: {
    addFavourite: (state, action) => {
      state.isLiked.push(action);
    },
  },
});

export const { addFavourite } = productSlice.actions;

export default productSlice.reducer;
