import React from "react";
import Product from "../components/Product";
import { useDispatch, useSelector } from "react-redux";
import { fetchProduct, setItems } from "../redux/Slices/productSlice";
import axios from "axios";

const url = "https://dummyjson.com/products";

async function fetchProducts() {
  try {
    const response = await fetch(url); // Отправляем GET-запрос
    if (!response.ok) {
      throw new Error(`Ошибка: ${response.status}`);
    }

    const data = await response.json(); // Обрабатываем ответ в формате JSON

    // Перебираем товары и выводим их
    data.products.forEach((product) => {
      console.log("Название:", product.title);
      console.log("Цена:", product.price);
      console.log("Изображение:", product.images[0]);

      // Добавим картинку на страницу
      const img = document.createElement("img");
      img.src = product.images[0];
      img.alt = product.title;
      img.style.width = "150px";
      document.body.appendChild(img);
    });
  } catch (error) {
    console.error("Ошибка при получении данных:", error);
  }
}

fetchProducts();

const Products = () => {
  const items = useSelector((state) => state.productSlice.items);
  const dispatch = useDispatch();

  return <div className="product_wrapper"></div>;
};

export default Products;
