import React from "react";
import styles from "./Sort.module.scss";
import { useDispatch, useSelector } from "react-redux";
import { setSort } from "../../redux/Slices/productSlice";
const Sort = () => {
  const { all, liked } = useSelector((state) => state.productSlice.sort);
  const sel = React.useRef();
  const dispatch = useDispatch();
  const onClickActice = () => {
    dispatch(setSort(sel));
  };
  return (
    <div className={styles.sort}>
      <ul className={styles.list}>
        <li
          ref={sel}
          onClick={onClickActice}
          className={all === true ? styles.active : styles.element}
        >
          Все карточки
        </li>
        <li
          ref={sel}
          onClick={onClickActice}
          className={liked === true ? styles.active : styles.element}
        >
          Понравилось
        </li>
      </ul>
    </div>
  );
};

export default Sort;
