import { useDispatch, useSelector } from "react-redux";
import ProductInfo from "./pages/productInfo";
import Products from "./pages/products";
import "./scss/app.scss";
import { Routes, Route } from "react-router-dom";
import { fetchProduct } from "./redux/Slices/productSlice";
import React from "react";
function App() {
  const i = useSelector((state) => state.productSlice.currentId);
  const items = useSelector((state) => state.productSlice.items);
  const dispatch = useDispatch();
  const getItems = async () => {
    dispatch(fetchProduct());
  };
  React.useEffect(() => {
    getItems();
  }, []);
  return (
    <div className="app">
      <Routes>
        <Route
          path={"test-task/products"}
          element={<Products items={items} />}
        ></Route>
        <Route
          path={`test-task/products/${i}`}
          element={<ProductInfo i={i} />}
        ></Route>
      </Routes>
    </div>
  );
}

export default App;
