import React from "react";
import styles from "./Sort.module.scss";
import { useDispatch, useSelector } from "react-redux";
import { setSort } from "../../redux/Slices/productSlice";
const Sort = () => {
  const active = useSelector((state) => state.productSlice.sort.name);
  const dispatch = useDispatch();
  const onClickActice = () => {
    dispatch(setSort());
  };
  return (
    <div className={styles.sort}>
      <ul className={styles.list}>
        <li onClick={onClickActice} className={active}>
          Все карточки
        </li>
        <li className={styles.element}>Понравилось</li>
      </ul>
    </div>
  );
};

export default Sort;
