import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";
import axios from "axios";

export const fetchProduct = createAsyncThunk(
  "product/fetchProducts",
  async () => {
    const url = "https://dummyjson.com/products";
    const { data } = await axios.get(url);
    return data;
  }
);

const initialState = {
  items: JSON.parse(localStorage.getItem("productItems")) || [],
  isLiked: [],
  sort: { name: "Все товары", property: "all" },
  currentId: 1,
};

export const productSlice = createSlice({
  name: "product",
  initialState,
  reducers: {
    setNewItem: (state, action) => {
      state.items.push(action.payload);
      localStorage.setItem("productItems", JSON.stringify(state.items));
    },
    setCurrentId: (state, action) => {
      state.currentId = action.payload;
    },
    addFavourite: (state, action) => {
      if (state.isLiked.find((obj) => obj.id === action.payload.id)) {
        const index = state.isLiked.indexOf(
          state.isLiked.find((obj) => obj.id === action.payload.id)
        );
        state.isLiked.splice(index, 1);
      } else {
        state.isLiked.push(action.payload);
      }
    },
    removeItem: (state, action) => {
      const index = state.items.indexOf(
        state.items.find((obj) => obj.id === action.payload.id)
      );
      const indexLiked = state.isLiked.indexOf(
        state.isLiked.find((obj) => obj.id === action.payload.id)
      );
      state.items.splice(index, 1);
      state.isLiked.splice(indexLiked, 1);
      localStorage.setItem("productItems", JSON.stringify(state.items));
    },
    setSort: (state, action) => {
      state.sort = action.payload;
    },
  },
  extraReducers: (builder) => {
    builder.addCase(fetchProduct.fulfilled, (state, action) => {
      state.items.push(action.payload.products);
      state.items = state.items.flat();
      localStorage.setItem("productItems", JSON.stringify(state.items));
    });
  },
});

export const { addFavourite, removeItem, setSort, setCurrentId, setNewItem } =
  productSlice.actions;

export default productSlice.reducer;
