import React, { useState } from "react";
import styles from "./Form.module.scss";
import { useDispatch, useSelector } from "react-redux";
import { setForm } from "../../redux/Slices/newProductSlice";
import { setNewItem } from "../../redux/Slices/productSlice";

const ProductForm = ({ onSubmit }) => {
  const formData = useSelector((state) => state.newProductSlice);
  const dispatch = useDispatch();
  React.useEffect(() => {
    const savedFormData = JSON.parse(localStorage.getItem("productForm"));
    if (savedFormData) {
      dispatch(setForm(savedFormData));
    }
  }, []);
  React.useEffect(() => {
    localStorage.setItem("productForm", JSON.stringify(formData));
  }, [formData]);
  const handleChange = (event) => {
    const { name, value } = event.target;
    dispatch(setForm({ ...formData, [name]: value }));
  };

  const handleFileChange = (event) => {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        dispatch(setForm({ ...formData, imageUrl: reader.result }));
      };
      reader.readAsDataURL(file);
    }
    dispatch(setForm({ ...formData, image: file }));
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    if (onSubmit) onSubmit(formData);
  };
  const handleSaveCard = () => {
    dispatch(setNewItem());
  };

  return (
    <div className={styles.wrapper}>
      <form
        onSubmit={handleSubmit}
        style={{ maxWidth: "400px", margin: "0 auto" }}
      >
        <div>
          <label>
            Название товара:
            <input
              type="text"
              name="title"
              value={formData.title}
              onChange={handleChange}
              required
            />
          </label>
        </div>
        <div>
          <label>
            Цена:
            <input
              type="number"
              name="price"
              value={formData.price}
              onChange={handleChange}
              required
            />
          </label>
        </div>
        <div>
          <label>
            Описание:
            <textarea
              name="description"
              value={formData.description}
              onChange={handleChange}
              rows="4"
              required
            />
          </label>
        </div>
        <div>
          <label>
            Картинка:
            <input
              type="file"
              accept="image/*"
              onChange={handleFileChange}
              required
            />
          </label>
        </div>
        <button onClick={handleSaveCard} type="submit">
          Сохранить
        </button>
      </form>
    </div>
  );
};

export default ProductForm;
