import { createSlice } from "@reduxjs/toolkit";

const initialState = {
  items: [1, 2, 3, 4],
};

export const productSlice = createSlice({
  name: "product",
  initialState,
  reducers: {
    increment: (state) => {},
  },
});

export const { increment, decrement, incrementByAmount } = productSlice.actions;

export default productSlice.reducer;
