import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";
import axios from "axios";

export const fetchProduct = createAsyncThunk(
  "product/fetchProducts",
  async () => {
    const url = "https://dummyjson.com/products";
    const { data } = await axios.get(url);
    return data;
    // const {id, title, images} = params
    // const response = await fetch(url);
    // const data = await response.json();
    // console.log(data.products);
  }
);

const initialState = {
  items: [],
  isLiked: [],
  likeState: false,
};

export const productSlice = createSlice({
  name: "product",
  initialState,
  reducers: {
    addFavourite: (state, action) => {
      console.log(state.isLiked);
      if (state.isLiked.find(action.payload)) {
        const indexDel = state.isLiked.indexOf(action.payload);
        state.isLiked.splice(indexDel, 1);
        state.likeState = false;
      } else {
        state.isLiked.push(action);
        state.likeState = true;
      }
    },
  },
  extraReducers: (builder) => {
    builder.addCase(fetchProduct.fulfilled, (state, action) => {
      state.items.push(action.payload.products);
      state.items = state.items.flat();
    });
  },
});

export const { addFavourite, setItems } = productSlice.actions;

export default productSlice.reducer;
