import React from "react";
import Product from "../components/Product";
import { useDispatch, useSelector } from "react-redux";
import { fetchProduct } from "../redux/Slices/productSlice";
import Sort from "../components/Sort";

const Products = () => {
  const { items, isLiked, sort } = useSelector((state) => state.productSlice);

  const dispatch = useDispatch();
  const getItems = async () => {
    await dispatch(fetchProduct());
  };
  React.useEffect(() => {
    getItems();
  }, []);

  return (
    <>
      <Sort />
      {sort.property === "all" && (
        <div className="product_wrapper">
          {items.length > 0
            ? items.map((item) => (
                <Product
                  key={item.id}
                  id={item.id}
                  title={item.title}
                  price={item.price}
                  image={item.thumbnail}
                />
              ))
            : "Loading data"}
        </div>
      )}
      {sort.propert === "liked" &&
        isLiked.map((obj) => console.log(...items[0]))}
      {/* <div className="product_wrapper">
        {items.length > 0
          ? items.map((item) => (
              <Product
                key={item.id}
                id={item.id}
                title={item.title}
                price={item.price}
                image={item.thumbnail}
              />
            ))
          : "Loading data"}
      </div> */}
    </>
  );
};

export default Products;
