import React from "react";
import Product from "../components/Product";
import { useSelector } from "react-redux";
// import { fetchProduct } from "../redux/Slices/productSlice";
import Sort from "../components/Sort";
import { Link } from "react-router-dom";

const Products = () => {
  const { items, isLiked, sort } = useSelector((state) => state.productSlice);
  const formData = useSelector((state) => state.newProductSlice);
  const dispatch = useDispatch();
  const getItems = async () => {
    dispatch(fetchProduct());
  };
  React.useEffect(() => {
    localStorage.setItem("productItems", JSON.stringify(items));
  }, [items]);

  React.useEffect(() => {
    if (!items || items.length === 0) {
      getItems();
    }
  }, [formData]);

  return (
    <div>
      <div className="header">
        <Sort />
        <Link to={"/test-task/create-product/"}>
          <button>Создать карточку</button>
        </Link>
      </div>
      {sort.property === "all" && (
        <div className="product_wrapper">
          {items.length > 0 ? (
            items.map((item) => (
              <Product
                key={item.id}
                id={item.id}
                title={item.title}
                price={item.price}
                thumbnail={item.thumbnail}
                imageUrl={item.imageUrl}
              />
            ))
          ) : (
            <span>Loading data</span>
          )}
        </div>
      )}
      {sort.property === "liked" && (
        <div className="product_wrapper">
          {isLiked.length > 0 ? (
            isLiked.map((obj) => (
              <Product
                key={obj.id}
                {...items.find((item) => item.id === obj.id)}
                imageUrl={formData.imageUrl}
              />
            ))
          ) : (
            <span>Вы еще ничего не добавили</span>
          )}
        </div>
      )}
    </div>
  );
};

export default Products;
