import React from "react";
import styles from "./EditForm.module.scss";

import { useDispatch, useSelector } from "react-redux";
import {
  setFormField,
  setFormImage,
} from "../../redux/Slices/replaceProductSlice";
import { replacedItem } from "../../redux/Slices/productSlice";

const EditFrom = ({ onSubmit, id }) => {
  const dispatch = useDispatch();
  const data = useSelector((state) => state.replaceProductSlice);
  const handleChange = (event) => {
    const { name, value } = event.target;
    dispatch(setFormField({ field: name, value }));
  };
  const handleImageChange = (event) => {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        dispatch(setFormField({ ...data, imageUrl: reader.result }));
      };
      reader.readAsDataURL(file);
    }
  };
  const handleSubmit = (event) => {
    event.preventDefault();
    handleSaveCard();
    if (onSubmit) onSubmit(data);
  };
  const handleSaveCard = () => {
    const products = JSON.parse(localStorage.getItem("productFormArray")) || [];
    const updatedFormData = { ...data };
    const productsUpdate = products.map((obj) =>
      obj.id === data.id ? { ...obj, ...updatedFormData } : obj
    );
    localStorage.setItem("productFormArray", JSON.stringify(productsUpdate));
    dispatch(replacedItem(products));
  };

  return (
    <div className={styles.wrapper}>
      <form
        onSubmit={handleSubmit}
        style={{ maxWidth: "400px", margin: "0 auto" }}
      >
        <div>
          <label>
            <input
              placeholder="Название товара:"
              type="text"
              name="title"
              value={data.title}
              onChange={handleChange}
              required
            />
          </label>
        </div>
        <div>
          <label>
            <input
              placeholder="Цена:"
              type="number"
              name="price"
              value={data.price}
              onChange={handleChange}
              required
            />
          </label>
        </div>
        <div>
          <label>
            <textarea
              placeholder="Описание:"
              name="description"
              value={data.description}
              onChange={handleChange}
              rows="4"
              required
            />
          </label>
        </div>
        <div>
          <label>
            Картинка:
            <input
              type="file"
              accept="image/*"
              onChange={handleImageChange}
              required
            />
          </label>
        </div>
        <button type="submit">Сохранить</button>
      </form>
    </div>
  );
};

export default EditFrom;
