import { useSelector, useDispatch } from "react-redux";
import { addFavourite } from "../../redux/Slices/productSlice";

const Product = () => {
  const dispatch = useDispatch();
  const onClickLike = (obj) => {
    dispatch(addFavourite(obj));
  };
