    <div className={styles.wrapper}>
      <div className={styles.img}>
        <img src="" alt="img" />
      </div>
      <div className={styles.buttons}>
        <img
          onClick={className === "like" ? "unlike" : "like"}
          src={className === "like" ? fillLike : like}
          alt="like"
        />
        <img src={close} alt="x" />
        <img src={stylus} alt="edit" />
      </div>
      <div className={styles.descr}>
        <h4>title</h4>
